    // Function to filter JSON data based on user input
    function filterData(query) {
        return jsonData.filter(item =>
            item.name.toLowerCase().includes(query.toLowerCase())
        );
    }
  
    // Function to display filtered results
    function displayResults(results, query) {
        searchResultBoxRoot.classList.remove("d-none");
        searchResults.innerHTML = '';
        results.forEach(result => {
            const li = document.createElement('li');
            const nameParts = result.name.split(new RegExp(`(${query})`, 'i'));
            li.innerHTML = nameParts.map(part => part.toLowerCase() === query.toLowerCase() ? `<span class="highlight">${part}</span>` : part).join('');
            li.addEventListener('click', () => {
                searchBox.value = result.name;
                searchResults.innerHTML = '';
                fetchDetails();
                searchResultBoxRoot.classList.add("d-none");
            });
            searchResults.appendChild(li);
        });
    }
  
    // Event listener for input changes
    searchBox.addEventListener('input', function() {
        const query = this.value;
        if (query.length === 0) {
            searchResults.innerHTML = '';
            detailsDiv.textContent = ''; // Clear details display for empty search
            detailsDiv.classList.add('d-none');
            return;
        }
        const results = filterData(query);
        displayResults(results, query);
        detailsDiv.textContent = ''; // Clear details display for new search
    });

    // Event listener to fetch details when button is clicked
    fetchButton.addEventListener('click', fetchDetails);
  
    // Event listener to fetch details when Enter key is pressed
    searchBox.addEventListener('keypress', function(event) {
        if (event.key === 'Enter') {
            fetchDetails();
        }
    });

    // Event listener to update details when toggle is changed
    toggleInfo.addEventListener('change', fetchDetails);

    // Function to fetch details or additional info based on toggle switch
    function fetchDetails() {
        const query = searchBox.value;
        if (!query) {
            alert("Please enter a search query.");
            return;
        }
        const result = jsonData.find(item => item.name.toLowerCase() === query.toLowerCase());
        if (result) {
            if (toggleInfo.checked) {
                detailsDiv.classList.remove('d-none');
                detailsDiv.innerHTML = `<br>${result.additional_info}`;
            } else {
                detailsDiv.classList.remove('d-none');
                detailsDiv.innerHTML = `<strong>${result.name}</strong><br>${result.details}`;
            }
        } else {
            detailsDiv.classList.remove('d-none');
            detailsDiv.textContent = "Details not found.";
        }
    }
